import { useEffect, useState } from 'react';
import axios from 'axios';
import './App.css';
import MovieCard from './components/MovieCard';
import 'bootstrap/dist/css/bootstrap.min.css';
import { Navbar, Container, Nav, Form, FormControl, Button } from 'react-bootstrap';

function App() {

  const API_URL = "https://api.themoviedb.org/3"

  const [movies, setMovies] = useState([])
  const [query, setQuery]=useState('');

  const fetchMovies = async () => {
    const {data: {results}} = await axios.get(`${API_URL}/discover/movie`, {
      params: {
        api_key: process.env.REACT_APP_MOVIE_API_KEY
      }
    })

    setMovies(results)
  }

  useEffect (() => {
fetchMovies()
  }, [])

  const renderMovies = () => (
    movies.map(movie => (
      <MovieCard 
      key={movie.id}
      movie={movie}
      />
    ))
  )

  const searchMovie = async(e)=>{
    e.preventDefault();
    console.log("Searching");
    try{
const url=`"https://api.themoviedb.org/3/search/movie?api_key=670ab1cd0ceaf592ba7be80c8098630f&query"=${query}`;
const res=await fetch(url);
const data= await res.json();
console.log(data);
setMovies(data.results);
    }
    catch(e){
      console.log(e);
    }
  }
  const changeHandler=(e)=>{
    setQuery(e.target.value);
  }
  return (
    <>
    <Navbar bg="dark" expand="lg" variant="dark">
    <Container fluid>
      <Navbar.Brand href="/home">MovieDb App</Navbar.Brand>
      <Navbar.Brand href="/home">Trending</Navbar.Brand>
<Navbar.Toggle aria-controls="nabarScroll"></Navbar.Toggle>
  <Navbar.Collapse id="navbarScroll">
    <Nav
    className="me-auto my-2 my-lg-3"
    style={{maxHeight: '100px'}}
    navbarScroll></Nav>

<Form className="d-flex" onSubmit={searchMovie}>
  <FormControl
  type="search"
  placeholder="Movie Search"
  className="me-2"
  aria-label="search"
  name="query"
  value={query} onChange={changeHandler}></FormControl>
  <Button variant="secondary" type="submit">Search</Button>
</Form>
</Navbar.Collapse>
    </Container>
    </Navbar>
    <div className="App">
      <h1> Hello Youtube </h1>
      <div className="container w-400 bg-dark d-flex justify-content-center">
    <div className="grid">
        {renderMovies()}
      </div>
      </div>
      </div>
      </>
  );
}

export default App;
